  return <form className='form-add-friend'>
    <label htmlFor="">üë®‚Äçüëß Friend name</label>
    <input type="text" />

    <label htmlFor="">üéÜ Image Url</label>
    <input type="text" />


    <Button>Add</Button>
  </form>
}



