import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];


function App() {
  let [friends, setFriends] = useState(initialFriends)
  return (
    <div className="app">
      <div className='sidebar'>
          <FriendsList friends={friends} />
      </div>
    </div>
  );
}


let FriendsList = ({friends}) => {
  return <ul className='sidebar'>
    {
      friends.map((friend) => <Friend friend={friend}/>)
    }
  </ul>
}

let Friend = ({friend}) => {
  return <li>
    <img src={friend.image} alt="Friend image" />
    <h3>{friend.name}</h3>
     <p>{friend.balance === 0 && (<p className=''>you and {friend.name} are even</p>) }</p>
    <p>{friend.balance < 0 && (<p className='red'>{friend.name} owes you {Math.abs(friend.balance)}€ </p>) }</p>
    <p>{friend.balance > 0 && (<p className='green'>You owe {friend.name} {friend.balance}€ </p>) }</p>
    <button>Select</button>
  </li>
}


export default App;
