import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

let Button  = ({children, onClick}) => {
  return <button className='button' onClick={onClick}>{children}</button>
}


function App() {
  let [friends, setFriends] = useState(initialFriends)
  let [showAddForm, setAddFrorm] = useState(false)
  let [selectedFriend, setSelectedFriend] = useState(null)

  let handleCurrentChange = (id) =>{
    setSelectedFriend(id)
  }

  let currenSelectedfriend = friends.filter((f)=> f.id === selectedFriend)

  let AddNewFriend = (newFriend) =>{
    setFriends([...friends, newFriend])
  }

  return <div className='app'>
    <div className='sidebar'>
      <FriendsList friends={friends} handleCurrentChange={handleCurrentChange}/>
      {
        showAddForm && <AddFriendForm AddNewFriend={AddNewFriend}/>
      }
      <Button onClick={()=> setAddFrorm((show) => !show)}>{(showAddForm?'Close':'Add Friend')}</Button>
    </div>
    <FormSplitAndBill currenSelectedfriend={currenSelectedfriend}/>
  </div>
}


let FriendsList = ({friends,handleCurrentChange}) =>{
  return <ul>
    {
    friends.map((friend)=> <Friend friend={friend} key={friend.id} handleCurrentChange={handleCurrentChange}/>)
    }
  </ul>
}

let Friend = ({friend,handleCurrentChange}) => {
  return <li>
    <img src={friend.image} alt={friend.name}  />
    <h3>{friend.name}</h3>
    {
      friend.balance < 0 && <p className='red'>You owe {friend.name} {Math.abs(friend.balance)}‚Ç¨</p>
    }
    {
      friend.balance > 0 && <p className='green'>{friend.name} owes you {friend.balance}‚Ç¨</p>
    }
    {
      friend.balance === 0 && <p>You and {friend.balance} are even</p>
    }
    <Button onClick={()=>{
      console.log('Clicked : ' , friend.id)
      handleCurrentChange(friend.id)
    }}>Select</Button>
  </li>
}

let AddFriendForm = ({AddNewFriend}) => {

  let handSubmit = (e) =>{
    e.preventDefault()
    let id = crypto.randomUUID()
    const newFriend = {
      id,
      name,
      image : `${imageUrl}?u=${id}`,
      balance:0
    }
    AddNewFriend(newFriend)
    setName("")
    setImageUrl("https://i.pravatar.cc/48")
  }

  let [name,setName] = useState("")
  let [imageUrl, setImageUrl] = useState("https://i.pravatar.cc/48")
  return <form className='form-add-friend' onSubmit={(e) => handSubmit(e)}>
    <label >üíÅ‚Äç‚ôÇÔ∏è Friend Name</label>
    <input type="text" onChange={(e) => setName(e.target.value)} value={name}/>
     <label >üéÜ Image Url</label>
    <input type="text" onChange={(e) => setImageUrl(e.target.value) } value={imageUrl}/>
    <Button>Add</Button>
  </form>
}

let FormSplitAndBill = ({currenSelectedfriend}) => {
  let [selected, setSelected] = useState(currenSelectedfriend)

  return <form className='form-split-bill'>
    <h2>Split the bill with {selected.name}</h2>
    <label >üí∞ Bill value</label>
    <input type="text" />
    <label >üíÅ‚Äç‚ôÇÔ∏è Your expense</label>
    <input type="text" />
    <label >üëß X'x expense</label>
    <input type="text" disabled/>
    <label >ü§ë Who's paying the bill?</label>
    <select >
      <option value="you">you</option>
      <option value="x">x</option>
    </select>
    <Button>Split Bill</Button>
  </form>
}


export default App;
